import { useState } from "react";
import { useClient } from "../../settings";
import { Grid ,Button} from "@material-tailwind/react";
import { BiMicrophone, BiMicrophoneOff, BiVideo, BiVideoOff, BiLogOut } from "react-icons/bi";

export default function Controls(props) {
  const client = useClient();
  const { tracks, setStart, setInCall } = props;
  const [trackState, setTrackState] = useState({ video: true, audio: true });

  const mute = async (type) => {
    if (type === "audio") {
      await tracks[0].setEnabled(!trackState.audio);
      setTrackState((ps) => {
        return { ...ps, audio: !ps.audio };
      });
    } else if (type === "video") {
      await tracks[1].setEnabled(!trackState.video);
      setTrackState((ps) => {
        return { ...ps, video: !ps.video };
      });
    }
  };

  const leaveChannel = async () => {
    await client.leave();
    client.removeAllListeners();
    tracks[0].close();
    tracks[1].close();
    setStart(false);
    setInCall(false);
  };

  return (
    <Grid container spacing={2} alignItems="center"  className="absolute flex place-content-center justify-items-center p-3 bg-gray-300">
      <Grid item>
        <Button
          variant="contained"
          color={trackState.audio ? "primary" : "secondary"}
          onClick={() => mute("audio")}
        >
          {trackState.audio ? <BiMicrophone style={{ fontSize: '24px' }} /> : <BiMicrophoneOff style={{ fontSize: '24px' }}/>}
        </Button>
      </Grid>
      <Grid item>
        <Button
          variant="contained"
          color={trackState.video ? "primary" : "secondary"}
          onClick={() => mute("video")}
        >
          {trackState.video ? <BiVideo style={{ fontSize: '24px' }}/> : <BiVideoOff style={{ fontSize: '24px' }}/>}
        </Button>
      </Grid>
      <Grid item>
        <Button
          variant="contained"
          color="default"
          onClick={() => leaveChannel()}
        >
          Leave
          <BiLogOut />
        </Button>
      </Grid>
    </Grid>
  );
}